    private async Task OnSave()
    {
        ShowWindow = false;
        if (Accion == "Add")
        {
            if (LstTPago.Exists(td => td.N_Tipo_Pago == SelectedTPago.N_Tipo_Pago))
            {
                //toast.ShowWarning("La descripción ya se encuentra en la lista...", "ATENCION");
                return;
            }

            var response = await srvTPago.CreateAsync(SelectedTPago);
            if (response.IsSuccess)
            {
                LstTPago.Add(response.Result);
                //toast.ShowSuccess("Los datos se almacenaron correctamente...", "ATENCION");
            }
            else
            {
                //toast.ShowError(response.Message, "ERROR");
            }
        }

        if (Accion == "Upd")// Metodo comportamiento extraño
        {

            var response = await srvTPago.UpdateAsync(SelectedTPago);
            if (response.IsSuccess)
            {
                LstTPago.Add(response.Result);
                var posicion = LstTPago.FindIndex(td => td.ID_Tipo_Pago == SelectedTPago.ID_Tipo_Pago);
                if (posicion == -1)
                {
                    //toast.ShowError("Se produjo un error inesperado...", "ERROR");
                }
                else
                {
                    LstTPago[posicion] = SelectedTPago;
                    Notification.Show(new NotificationModel()
                    {
                        Text = "Los datos se almacenaron correctamente...",
                        ThemeColor = "primary",
                        ShowIcon = true
                    });
                }
            }
            else
            {
                //toast.ShowError(response.Message, "ERROR");
            }
        }

        if (Accion == "Del")
        {
            var response = await srvTPago.DeleteAsync(SelectedTPago);
            if (response.IsSuccess)
            {
                LstTPago = await srvTPago.GetTipoPagos();
                //toast.ShowSuccess("Los datos se eliminaron correctamente...", "ATENCION");
            }
            else
            {
                //toast.ShowError(response.Message, "ERROR");
            }
        }

        
        LstTPago = await srvTPago.GetTipoPagos();

        StateHasChanged();
    }

